import { useState } from 'react'
import axios from 'axios'
import Button from '@/components/shared/Button'

interface FridgeItemDetailsProps {
  ingredient: {
    id: number
    name: string
    iconNumber: number
    registrationDate: string
    expirationDate: string
  }
}

function FridgeItemDetails({ ingredient }: FridgeItemDetailsProps) {
  const [selectedIcon, setSelectedIcon] = useState<number>(
    ingredient.iconNumber,
  ) // ÏïÑÏù¥ÏΩò ÎÑòÎ≤Ñ ÏÉÅÌÉú
  const [registrationDate, setRegistrationDate] = useState<string>(
    ingredient.registrationDate,
  ) // Îì±Î°ùÏùº ÏÉÅÌÉú
  const [expirationDate, setExpirationDate] = useState<string>(
    ingredient.expirationDate,
  ) // Ïú†ÌÜµÍ∏∞Ìïú ÏÉÅÌÉú

  const handleIconSelect = (iconValue: number) => {
    setSelectedIcon(iconValue) // ÏïÑÏù¥ÏΩò ÎÑòÎ≤Ñ Ï†ÄÏû•
  }

  const handleSave = () => {
    // ÏÑúÎ≤ÑÎ°ú ÏÑ†ÌÉùÎêú Îç∞Ïù¥ÌÑ∞Î•º Ï†ÑÏÜ°ÌïòÎäî Î°úÏßÅÏùÑ Ï∂îÍ∞ÄÌï† Ïàò ÏûàÏäµÎãàÎã§.
    console.log('ÏÑ†ÌÉùÎêú ÏïÑÏù¥ÏΩò Î≤àÌò∏:', selectedIcon)
    console.log('Îì±Î°ùÏùº:', registrationDate)
    console.log('Ïú†ÌÜµÍ∏∞Ìïú:', expirationDate)

    // ÏòàÏãúÎ°ú AxiosÎ•º ÏÇ¨Ïö©Ìï¥ ÏÑúÎ≤ÑÎ°ú Îç∞Ïù¥ÌÑ∞Î•º Ï†ÑÏÜ°ÌïòÎäî Î∞©Ïãù
    axios
      .post('/api/save-ingredient-details', {
        id: ingredient.id,
        iconNumber: selectedIcon,
        registrationDate,
        expirationDate,
      })
      .then(() => {
        alert('Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§!')
      })
      .catch((error) => {
        console.error('Ï†ÄÏû• Ï§ë Ïò§Î•ò Î∞úÏÉù:', error)
      })
  }

  return (
    <div className="mt-6">
      {/* ÏïÑÏù¥ÏΩò ÏÑ†ÌÉù ÏòÅÏó≠ */}
      <div className="grid grid-cols-4 gap-4 mb-4">
        {[1, 2, 3, 4, 5, 6, 7, 8].map((icon) => (
          <button
            key={icon}
            onClick={() => handleIconSelect(icon)}
            className={`p-2 border-2 rounded-lg ${
              selectedIcon === icon ? 'border-brown-500' : 'border-gray-300'
            }`}
          >
            {/* Placeholder for icons - Here you can replace with actual SVG icons */}
            <span className="text-2xl">
              {icon === 1 ? 'ü•¶' : icon === 2 ? 'ü•ö' : icon === 3 ? 'ü•õ' : 'üçñ'}
            </span>
          </button>
        ))}
      </div>

      {/* Ïû¨Î£å Ïù¥Î¶Ñ */}
      <h2 className="text-center text-xl font-bold mb-4">{ingredient.name}</h2>

      {/* Îì±Î°ùÏùºÍ≥º Ïú†ÌÜµÍ∏∞Ìïú ÏûÖÎ†• ÌïÑÎìú */}
      <div className="mb-4">
        <label className="block text-sm font-semibold mb-1">Îì±Î°ùÎÇ†Ïßú</label>
        <input
          type="date"
          value={registrationDate}
          onChange={(e) => setRegistrationDate(e.target.value)} // Îì±Î°ùÏùº ÏóÖÎç∞Ïù¥Ìä∏
          className="w-full p-2 border border-gray-300 rounded-lg"
        />
      </div>
      <div className="mb-6">
        <label className="block text-sm font-semibold mb-1">Ïú†ÌÜµÍ∏∞Ìïú</label>
        <input
          type="date"
          value={expirationDate}
          onChange={(e) => setExpirationDate(e.target.value)} // Ïú†ÌÜµÍ∏∞Ìïú ÏóÖÎç∞Ïù¥Ìä∏
          className="w-full p-2 border border-gray-300 rounded-lg"
        />
      </div>

      {/* Ï†ÄÏû• Î≤ÑÌäº */}
      <div className="text-center">
        <Button label="Ï†ÄÏû•" size="full" onClick={handleSave} />
      </div>
    </div>
  )
}

export default FridgeItemDetails
