import { useEffect, useState } from 'react'
import axios from 'axios'
import { Ingredient } from '@models/ingredient'
import IngredientItem from '@/components/fridge/shared/ingredientItem'
import useUser from '@hooks/auth/useUser'
import Button from '@/components/shared/Button'
import Modal from '@shared/Modal'
import FridgeForm from '@/components/fridge/FridgeForm'

function FridgePage() {
  const { user } = useUser()
  const [ingredients, setIngredients] = useState<Ingredient[]>([])
  const [loading, setLoading] = useState<boolean>(true)
  const [isModalOpen, setIsModalOpen] = useState<boolean>(false) // 모달 상태

  useEffect(() => {
    if (user && user.id) {
      axios
        .get(
          `https://3ccfb3c6-7a46-4902-b117-a23e940861d2.mock.pstmn.io/api/fridge-ingredients`,
          {
            params: { userId: user.id },
          },
        )
        .then((response) => {
          setIngredients(response.data)
          setLoading(false)
        })
        .catch((error) => {
          console.error('Error fetching data:', error)
          setLoading(false)
        })
    }
  }, [user])

  const handleOpenModal = () => {
    setIsModalOpen(true) // 모달 열기
  }

  const handleCloseModal = () => {
    setIsModalOpen(false) // 모달 닫기
  }

  if (loading) {
    return <div>Loading...</div>
  }

  return (
    <div className="relative h-full w-full min-h-[500px]">
      <div className="flex ">
        <Button
          label="재료 등록하기"
          size="small"
          onClick={handleOpenModal}
        ></Button>{' '}
        {/* 모달 열기 */}
      </div>
      <div className="my-6"></div>
      <div className="grid grid-cols-1 gap-4">
        {ingredients.length > 0 ? (
          ingredients.map((ingredient) => (
            <IngredientItem key={ingredient.id} ingredient={ingredient} />
          ))
        ) : (
          <div>냉장고에 저장된 재료가 없습니다.</div>
        )}
      </div>
      <div className="absolute w-full bottom-1">
        <Button
          label="나의 맞춤 레시피"
          size="full"
          color="bg-green-100"
        ></Button>
      </div>

      {/* 모달 */}
      <Modal isOpen={isModalOpen} onClose={handleCloseModal}>
        <FridgeForm />
      </Modal>
    </div>
  )
}

export default FridgePage
