import React from 'react'

interface ButtonProps {
  label: string
  onClick?: () => void
  type?: 'button' | 'submit' | 'reset'
  variant?: 'primary' | 'secondary' | 'danger'
}

const Button: React.FC<ButtonProps> = ({
  label,
  onClick,
  type = 'button',
  variant = 'primary',
}) => {
  const baseStyle =
    'px-4 py-2 font-semibold rounded-lg focus:outline-none focus:ring transition duration-300 ease-in-out'
  const primaryStyle = 'bg-brown100 text-white hover:bg-yellow focus:ring-green'
  const secondaryStyle =
    'bg-Salmon-100 text-brown-100 hover:bg-Salmon-200 focus:ring-Salmon-100'
  const dangerStyle = 'bg-red text-white hover:bg-red-500 focus:ring-red'

  const buttonStyle =
    variant === 'primary'
      ? primaryStyle
      : variant === 'secondary'
        ? secondaryStyle
        : dangerStyle

  return (
    <button
      type={type}
      onClick={onClick}
      className={`${baseStyle} ${buttonStyle}`}
    >
      {label}
    </button>
  )
}

export default Button
