  const [expirationDate, setExpirationDate] = useState<string>(
    ingredient.expirationDate,
  ) // Ïú†ÌÜµÍ∏∞Ìïú ÏÉÅÌÉú

  const handleIconSelect = (iconValue: number) => {
    setSelectedIcon(iconValue) // ÏïÑÏù¥ÏΩò ÎÑòÎ≤Ñ Ï†ÄÏû•
  }

  const handleSave = () => {
    // ÏÑúÎ≤ÑÎ°ú ÏÑ†ÌÉùÎêú Îç∞Ïù¥ÌÑ∞Î•º Ï†ÑÏÜ°ÌïòÎäî Î°úÏßÅÏùÑ Ï∂îÍ∞ÄÌï† Ïàò ÏûàÏäµÎãàÎã§.
    console.log('ÏÑ†ÌÉùÎêú ÏïÑÏù¥ÏΩò Î≤àÌò∏:', selectedIcon)
    console.log('Îì±Î°ùÏùº:', registrationDate)
    console.log('Ïú†ÌÜµÍ∏∞Ìïú:', expirationDate)

    // ÏòàÏãúÎ°ú AxiosÎ•º ÏÇ¨Ïö©Ìï¥ ÏÑúÎ≤ÑÎ°ú Îç∞Ïù¥ÌÑ∞Î•º Ï†ÑÏÜ°ÌïòÎäî Î∞©Ïãù
    axios
      .post('/api/save-ingredient-details', {
        id: ingredient.id,
        iconNumber: selectedIcon,
        registrationDate,
        expirationDate,
      })
      .then(() => {
        alert('Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§!')
      })
      .catch((error) => {
        console.error('Ï†ÄÏû• Ï§ë Ïò§Î•ò Î∞úÏÉù:', error)
      })
  }

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold">{ingredient.name} ÏÉÅÏÑ∏Ï†ïÎ≥¥</h2>
      <div className="my-4">
        <label>Îì±Î°ùÏùº:</label>
        <input
          type="date"
          value={registrationDate}
          onChange={(e) => setRegistrationDate(e.target.value)} // Îì±Î°ùÏùº ÏóÖÎç∞Ïù¥Ìä∏
          className="border p-2 mb-4 rounded"
        />
      </div>
      <div className="my-4">
        <label>Ïú†ÌÜµÍ∏∞Ìïú:</label>
        <input
          type="date"
          value={expirationDate}
          onChange={(e) => setExpirationDate(e.target.value)} // Ïú†ÌÜµÍ∏∞Ìïú ÏóÖÎç∞Ïù¥Ìä∏
          className="border p-2 mb-4 rounded"
        />
      </div>
      <div className="my-4">
        <h3 className="font-semibold">ÏïÑÏù¥ÏΩò ÏÑ†ÌÉù</h3>
        <div className="flex space-x-2">
          {/* Ïó¨Îü¨ ÏïÑÏù¥ÏΩò Ï§ë ÏÑ†ÌÉùÌïòÎäî ÏòàÏãú, ÏïÑÏù¥ÏΩò ÎÑòÎ≤ÑÎ•º valueÎ°ú ÏÇ¨Ïö© */}
          <button
            onClick={() => handleIconSelect(1)}
            className={`p-2 ${selectedIcon === 1 ? 'border border-blue-500' : ''}`}
          >
            ü•¶ (1)
          </button>
          <button
            onClick={() => handleIconSelect(2)}
            className={`p-2 ${selectedIcon === 2 ? 'border border-blue-500' : ''}`}
          >
            üçÖ (2)
          </button>
          <button
            onClick={() => handleIconSelect(3)}
            className={`p-2 ${selectedIcon === 3 ? 'border border-blue-500' : ''}`}
          >
            ü•ï (3)
