import { useState } from 'react'
import axios from 'axios'
import Button from '@/components/shared/Button'

interface FridgeItemDetailsProps {
  ingredient: {
    id: number
    name: string
    iconNumber: number
    registrationDate: string
    expirationDate: string
  }
}

function FridgeItemDetails({ ingredient }: FridgeItemDetailsProps) {
  const [selectedIcon, setSelectedIcon] = useState<number>(
    ingredient.iconNumber,
  ) // 아이콘 넘버 상태
  const [registrationDate, setRegistrationDate] = useState<string>(
    ingredient.registrationDate,
  ) // 등록일 상태
  const [expirationDate, setExpirationDate] = useState<string>(
    ingredient.expirationDate,
  ) // 유통기한 상태

  const handleIconSelect = (iconValue: number) => {
    setSelectedIcon(iconValue) // 아이콘 넘버 저장
  }

  const handleSave = () => {
    // 서버로 선택된 데이터를 전송하는 로직을 추가할 수 있습니다.
    console.log('선택된 아이콘 번호:', selectedIcon)
    console.log('등록일:', registrationDate)
    console.log('유통기한:', expirationDate)

    // 예시로 Axios를 사용해 서버로 데이터를 전송하는 방식
    axios
      .post('/api/save-ingredient-details', {
        id: ingredient.id,
        iconNumber: selectedIcon,
        registrationDate,
        expirationDate,
      })
      .then(() => {
        alert('저장되었습니다!')
      })
      .catch((error) => {
        console.error('저장 중 오류 발생:', error)
      })
  }

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold">{ingredient.name} 상세정보</h2>
      <div className="my-4">
        <label>등록일:</label>
        <input
          type="date"
          value={registrationDate}
          onChange={(e) => setRegistrationDate(e.target.value)} // 등록일 업데이트
          className="border p-2 mb-4 rounded"
        />
      </div>
      <div className="my-4">
        <label>유통기한:</label>
        <input
          type="date"
          value={expirationDate}
          onChange={(e) => setExpirationDate(e.target.value)} // 유통기한 업데이트
          className="border p-2 mb-4 rounded"
        />
      </div>
      <div className="my-4">
        <h3 className="font-semibold">아이콘 선택</h3>
        <div className="flex space-x-2">
          {/* 여러 아이콘 중 선택하는 예시, 아이콘 넘버를 value로 사용 */}
          <button
            onClick={() => handleIconSelect(1)}
            className={`p-2 ${selectedIcon === 1 ? 'border border-blue-500' : ''}`}
          >
            🥦 (1)
          </button>
          <button
            onClick={() => handleIconSelect(2)}
            className={`p-2 ${selectedIcon === 2 ? 'border border-blue-500' : ''}`}
          >
            🍅 (2)
          </button>
          <button
            onClick={() => handleIconSelect(3)}
            className={`p-2 ${selectedIcon === 3 ? 'border border-blue-500' : ''}`}
          >
            🥕 (3)
          </button>
        </div>
        {selectedIcon && (
          <p className="mt-4">선택된 아이콘 번호: {selectedIcon}</p>
        )}
      </div>
      <Button label="저장" size="small" onClick={handleSave} />
    </div>
  )
}

export default FridgeItemDetails
