import { Ingredient } from '@models/ingredient'

interface IngredientItemProps {
  ingredient: Ingredient
  onClick: () => void
}

const IngredientItem: React.FC<IngredientItemProps> = ({
  ingredient,
  onClick,
}) => {
  const icons: { [key: number]: JSX.Element } = {
    // 아이콘 SVG들 정의
  }

  const icon = icons[ingredient.iconNumber] || (
    <svg
      width="50"
      height="50"
      viewBox="0 0 24 24"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
    >
      <circle cx="12" cy="12" r="10" fill="gray" />
    </svg>
  )

  return (
    <div
      className="material-item bg-white mb-4 rounded-lg flex p-4 cursor-pointer"
      onClick={onClick} // 클릭 핸들러 추가
    >
      <div className="flex justify-center items-center border border-brown-100 p-2 rounded-lg mr-6">
        {icon}
      </div>
      <div>
        <strong>{ingredient.name}</strong>
        <div className="text-sm">
          <p>등록날짜: {ingredient.registrationDate}</p>
          <p>유통기한: {ingredient.expirationDate}</p>
        </div>
      </div>
    </div>
  )
}

export default IngredientItem
