import { forwardRef, InputHTMLAttributes } from 'react'

interface TextFieldProps extends InputHTMLAttributes<HTMLInputElement> {
  label?: React.ReactNode
  hasError?: boolean
  helpMessage?: React.ReactNode
}

const TextField = forwardRef<HTMLInputElement, TextFieldProps>(
  function TextField({ label, hasError }, ref) {
    const labelColor = hasError ? 'red' : 'text-brown-100'
    return (
      <div>
        {label ? (
          <div className={`${labelColor} font-small`}>{label}</div>
        ) : null}
        <input
          type="text"
          placeholder="이메일"
          className="mt-1 block w-full px-3 py-2 bg-white border border-brown rounded-md text-sm shadow-sm placeholder-grey
focus:outline-none focus:border-brown-300 focus:ring-1 focus:ring-brown-300
disabled:bg-cream disabled:text-cream disabled:border-brown-200 disabled:shadow-none
invalid:border-red invalid:text-red
focus:invalid:red focus:invalid:red
"
        />
      </div>
    )
  },
)

export default TextField
