import React from 'react'

interface SpacingProps {
  size: number // 크기 (2, 4, 6, 8 등)
  direction?: 'vertical' | 'horizontal' // 방향 설정
}

const Spacing: React.FC<SpacingProps> = ({ size, direction = 'vertical' }) => {
  // Tailwind에서 사용할 수 있는 간격 크기만 미리 지정
  const spacingClass = {
    2: '2',
    4: '4',
    6: '6',
    8: '8',
    10: '10',
  }[size]

  if (!spacingClass) {
    return null
  }

  // 방향에 따른 클래스 설정
  const classNames =
    direction === 'horizontal' ? `mx-${spacingClass}` : `mb-${spacingClass}`

  return <div className={classNames}></div>
}

export default Spacing
