import instance from '@/util/axios'
import { useEffect, useState } from 'react'
import Button from '../shared/Button'
import { Ingredient } from '@models/ingredient'

function FridgeForm() {
  const [ingredients, setIngredients] = useState<Ingredient[]>([])
  const [loading, setLoading] = useState<boolean>(true)

  useEffect(() => {
    instance
      .get(
        'https://3ccfb3c6-7a46-4902-b117-a23e940861d2.mock.pstmn.io/api/new-ingredients',
      )
      .then((response) => {
        setIngredients(response.data)
        setLoading(false)
      })
      .catch((error) => {
        console.error('Error fetching data:', error)
        setLoading(false)
      })
  }, [])
  return (
    <div>
      <div className="flex justify-center items-center ">
        <input
          placeholder="재료명을 입력해 검색해보세요"
          className="w-3/4 h-10 border border-brown-100 p-2 m-2 rounded-lg"
        />
        <Button label="검색" />
      </div>
      <div>
        {ingredients.length > 0 ? (
          ingredients.map((ingredient) => (
            <div key={ingredient.id} ingredient={ingredient}></div>
          ))
        ) : (
          <div>냉장고에 저장된 재료가 없습니다.</div>
        )}
      </div>
    </div>
  )
}

export default FridgeForm
